import nodemailer from 'nodemailer';
import { EMAIL, EMAIL_APP_PASSWORD } from '../config/environment'

const transporter = nodemailer.createTransport({
    service: 'gmail', // o utiliza otro servicio de correo SMTP
    auth: {
        user: EMAIL, // Tu dirección de correo electrónico
        pass: EMAIL_APP_PASSWORD, // La contraseña del correo o App Password si usas 2FA
    },
});

export const sendMailRegister = async (email: string, password: string) => {
    const mailOptions = {
        from: EMAIL,
        to: email,
        subject: 'Registro Exitoso en Break & Focus',
        html: ` 
        <a href="https://breakandfocus.onrender.com/" target="_blank"><img src="data:image/svg+xml;base64," alt="Rectángulo azul" /></a>
        
    <p>¡Hola!</p>
    
    <p>Te damos la bienvenida a <strong>Break & Focus</strong>. Tu registro ha sido exitoso, y ahora puedes disfrutar de todos nuestros servicios.</p>
    
    
    
    <p>A continuación, encontrarás tus datos de acceso:</p>
    
    <p>Usuario: <strong>${email}</strong><br>
    Contraseña: <strong>${password}</strong></p>

    <p>Si tienes alguna pregunta o necesitas ayuda, no dudes en contactarnos.</p>
    
    <p>¡Gracias por unirte a <strong>Break & Focus</strong>!</p>

    <a href="https://breakandfocus.onrender.com/" target="_blank">Ir a Break & Focus</a>
    
    <p>Atentamente,<br>
    El equipo de Break & Focus</p>
    
    <hr>

    <p>---<br>
    Este es un correo autogenerado, por favor no respondas a este mensaje.</p>`
    };


    try {
        const info = await transporter.sendMail(mailOptions);
        console.log('Correo enviado:', info);
    } catch (error) {
        console.error('Error al enviar correo:', error);
    }
};

